// script para mostrar total sellos incauca en la app web de sellos polipropileno 
totalSellosPP();
function totalSellosPP(){
 $.ajax({
    url: "http://127.0.0.1:5000/get_api_total_pp",
    type:"GET"
 }).done(function(resp){
    $("#totalp").text(parseFloat(resp["totalp"]).toLocaleString());
 })
};
