//script de tiempo de espera de la alerta de la aplicación web de sellos polipropileno
document.addEventListener('DOMContentLoaded', function () {
    setTimeout(function () {
    let alerts = document.querySelectorAll('.alert')
    alerts.forEach(function (alert) {
        alert.style.opacity = '0'
        setTimeout(function () {
        alert.remove()
        }, 2000)
    })
    }, 2000)
});
// Script para mostrar el modal de confirmación al eliminar un sello de la aplicación web de sellos polipropileno
document.querySelectorAll('.btn-danger').forEach(function (button) {
    button.addEventListener('click', function (event) {
        event.preventDefault()
        let url = this.getAttribute('href')
        Swal.fire({
            title: '¿Estás seguro?',
            text: "¡Vas a eliminar este sello!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminar'
        }).then((result) => {
            if (result.isConfirmed) {
                window.location.href = url
            }
        })
    })
});
// Script para exportar la tabla a Excel en la aplicación web de sellos polipropileno
$(document).ready(function() {
    $("#btnExport").click(function() {
        $("#tabla").table2excel({
            exclude: ".noExl", // Exclude elements with this class
            name: "sellosPP",
            filename: "sellos_pp.xls", // Desired filename
            fileext: ".xls",
            exclude_img: true, // Exclude images
            exclude_links: true, // Exclude links
            exclude_inputs: true // Exclude form inputs
        });
    });
});